import React, { useState } from "react";
import { Button, Input, Container } from "reactstrap";

const ChatWindow = () => {
  const [message, setMessage] = useState("");

  const placeholderConversation = Array.from({ length: 5 }, (_, i) => ({
    id: i,
    sender: i % 2 === 0 ? "Person Name 1" : "Person Name 2",
    message: `This is a long placeholder message for testing the chat window. This is message ${
      i + 1
    }.`,
  }));

  const handleChange = (event) => {
    setMessage(event.target.value);
  };

  const handleSend = () => {
    // handle send message
    setMessage("");
  };

  return (
    <Container>
      {placeholderConversation.map((item) => (
        <div key={item.id}>
          <strong>{item.sender}</strong>
          <p>{item.message}</p>
        </div>
      ))}
      <Input
        type='text'
        value={message}
        onChange={handleChange}
        placeholder='Type a message...'
      />
      <Button color='warning' onClick={handleSend}>
        Send
      </Button>
    </Container>
  );
};

export default ChatWindow;
